const Parent = (): JSX.Element => {
    //inizializzo useState a 0 per il counter
    const [counter, setCounter]= useState<Number>(0);
    //let c = 0;
    //in questo caso setCopunter modifica il lavore dato in precendenza
    const handNumber = ():void => {
        counter= counter+1;
        console.log("i'm parent", counter)
    }
    //console.log('I\'m Parent One', counter);
    return <div onClick={handNumber}>
        <div> 
        <p>Hai cliccato {counter} volte</p>
      <button onClick={() => setCounter(counter)}>
        Cliccami
      </button>
        <ChildOne/>

        </div>
    </div>
}

export default Parent
