import React, { ChangeEvent } from 'react';
import { Col, Container, Row } from 'react-bootstrap';


interface Props {
    listaProps: string[];
};

export const FormList = ({ listaProps }: Props) => {
    const clickButton = (e: ChangeEvent<HTMLInputElement>): void => {
        const elementSelected = document.getElementById(e.target.value);
        if (elementSelected) {
            //e.target bottone premuto
            //e.target.value è il valore del bottone premuto
            elementSelected.style.display = e.target.checked ? "block" : "none";
        }
    };

    return (
        <Container>
            <Row>
                <Col md={4}>
                    <h5>Lista: </h5>
                        {listaProps.map((item) => (
                            <p key="{item}" id={item}>
                                {item}

                            </p>
                        ))}
                    {listaProps.map((items)=>(<input type="checkbox" value={items} onChange={clickButton}/>))}
                </Col>
                <Col md={{ span: 4, offset: 4 }}>
                    Form
                        <form>
                        <label>
                            <input type="text" name="nome" />
                        </label>
                        <input type="submit" value="Submit" />
                    </form>
                </Col>
            </Row>
        </Container>

    )
}


export default FormList;
