import React, { useState, useRef } from 'react';
import { Modal, Col, Container, Row, Form} from 'react-bootstrap';
// interface Props {
//     [key: string]: any;
//     // onChange: (e:ChangeEvent<HTMLInputElement>) => void
// };


const formList = () => {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [newListItem, setNewListItem] = useState([]);
    const [setShow] = useState(false);
    const input = useRef();

    var addToList = e => {
        e.preventDefault();
        setNewListItem([...newListItem, input.current.value]);
      };
    return <>
        <section>
            <Container>
                <Row>
                    <Col xs lg="4">
                        Form
                        <form onSubmit={addToList}>
          <Modal.Body>
            <Form.Group>
              <Form.Label>Item</Form.Label>
              <br />
              <Form.Control type="text" ref={input} placeholder="Normal text" />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button type="sumbit">Add to List</Button>
          </Modal.Footer>
        </form>
                        <ul>
        {newListItem.map((item: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined, b: React.Key | null | undefined) => (
          <li key={b}>{item}</li>
        ))}
      </ul>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
}

export default formList
