import React, { useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import Select from "react-select";
interface Props {
    [key: string]: any;
    // data: string[],
    // isActive: boolean
    // element: { id: string, age: number},
    // action: ()=> void,
    //callback: <T>(data: {[key: string]: any}) => T ,
}
// oggetto di ritorno -> callback<string>({id: 0});

const ChildOne = (props: Props): JSX.Element => {
    //console.log('I \'m ChildOne')
const lista = ["Luigi ", "Gina ", "Maria ", "Pina "]
const [selectedOption, setSelectedOption] = useState(null);

    return <>
        <div>
            <Container>
                <Row>
                    <Col xs lg="4" >
                        Lista
                        <ul>
                             {lista.map((item) => <li>{item}</li>)}
                        </ul>
                    </Col>
                </Row>
            </Container>
            <div className="App">
      <Select
        defaultValue={selectedOption}
        onChange={setSelectedOption}
        options={lista}
      />
    </div>
  );
        </div>

    </>
}
export default ChildOne
