};

const formList = (props: Props): JSX.Element => {
    const lista = [
        {
            id: 'a',
            firstname: 'Gina'
        },
        {
            id: 'b',
            firstname: 'Pina'
        },
        {
            id: 'c',
            firstname: 'Maria'
        },
    ];
    return <>
        <section>
            <Container>
                <Row>
                    <Col xs lg="4">
                        Form
                        <form>
                            <label>
                                <input type="text" name="nome" />
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                    </Col>
                    <Col xs lg="4" >
                        Lista
                        <ul>
                            {lista.map((item) => (
                                <li key={item.id}>
                                    <span>{item.firstname}</span>
                                    <button type="button" onClick={handleRemove}>
            Remove
          </button>
                                </li>
                            ))}
                        </ul>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
}

export default formList
