import React, { useEffect, useState } from 'react';
import { render } from 'react-dom';
import { _get } from '../Services/_get';



export const searchApi = (): JSX.Element => {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [users, setUsers] = useState<string>();

    const componentDidMount = () =>{
        fetch("https://jsonplaceholder.typicode.com/users")
          .then(res => res.json())
          .then(
            result => {
              this.setState({
                data: result
              });
            },
            error => {
              console.log(error);
            }
          );
    }


    const render = () =>{
        return (
          <>
            <div>
              <h3>Using API call</h3>
              {this.state.data &&
                this.state.data.length > 0 &&
                this.state.data.map((item: { name: string | number | boolean | {} | React.ReactElement<any, string | React.JSXElementConstructor<any>> | React.ReactNodeArray | React.ReactPortal | null | undefined; }, i: React.Key | null | undefined) => (
                  <li key={i}>
                    <span>Email : {item.name}</span>
                  </li>
                ))}
            </div>
          </>
        );
    }

    // eslint-disable-next-line react-hooks/rules-of-hooks
    useEffect(() => {

        (async () => {
            const response = await _get(null);
            console.log('response api ', response);
            setUsers(await response);
        })();
    }, []);

    return (
        <>
            <p>we have {users?.length} users</p>
            {}
        </>
    );

};

export default searchApi
