import React from 'react';
import { ChangeEvent } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import formList from '../Form';

const lista = () => {
    let array = [
        {
            id: 'a',
            firstname: 'Gina'
        },
        {
            id: 'b',
            firstname: 'Pina'
        },
        {
            id: 'c',
            firstname: 'Maria'
        },
    ];

    const clickButton = (e: ChangeEvent<HTMLInputElement>): void => {
        const elementSelected = document.getElementById(e.target.value);
        if (elementSelected) {
          //e.target bottone premuto
          //e.target.value è il valore del bottone premuto
          elementSelected.style.display = e.target.checked ? "block" : "none";
        }
      };

    return <>
        <div>
            <Container className="App-space">
                <Row>
                    <Col xs={6}>
                        <ul>
                            {array.map((item) => (
                                <li key={item.id}>
                                    <span>{item.firstname}</span>
                                    <input style={{border: "none", background: "white"}}  type="checkbox" onChange={clickButton} />
                                </li>
                            ))}
                            </ul>
                    </Col>
                </Row>
            </Container>
        </div>

    </>
}
export default lista
