            id: 'a',
            firstname: 'Gina'
        },
        {
            id: 'b',
            firstname: 'Pina'
        },
        {
            id: 'c',
            firstname: 'Maria'
        },
    ];

    // const clickButton = (e: ChangeEvent<HTMLInputElement>): void => {
