    return <>
        <div>
            <Container className="App-space">
                <Row>
                    <Col xs={6}>
                        <ul>
                            {array.map((item) => (
                                <li key={item.id}>
                                    <span>{item.firstname}</span>
                                    <button style={{ border: "none", background: "white" }} type="button" onClick={() => formList}>
                                        ❌
                                    </button>
                                </li>
                            ))}
                        </ul>
                    </Col>

                </Row>
            </Container>
        </div>

    </>
}
export default lista
