import React from 'react';

import { Col, Container, Row } from 'react-bootstrap';
//import lista from '../ChildOne';
// interface Props {
//     listaProps: string[];
//     // onChange: (e:ChangeEvent<HTMLInputElement>) => void
// };

const formList = (props: Props): JSX.Element => {
    const lista = () => {
        let array = [
            {
                id: 'a',
                firstname: 'Gina'
            },
            {
                id: 'b',
                firstname: 'Pina'
            },
            {
                id: 'c',
                firstname: 'Maria'
            },
        ];
    return <>
        <section>
            <Container>
                <Row>
                    <Col xs lg="4">
                        Form
                        <form>
                            <label>
                                <input type="text" name="nome" />
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                    </Col>
                    <Col xs lg="4" >
                        Lista
                        <ul>
                            {array.map((item) => (
                                <li key={item.id}>
                                    <span>{item.firstname}</span>
                                    <button style={{border: "none", background: "white"}} type="button" onClick={() => formList}>
                                    ‚ùå
                                    </button>
                                </li>
                            ))}
                            </ul>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
}
}

export default formList
