import React, { useRef, useState } from 'react';

import { Col, Container, Row } from 'react-bootstrap';
interface Props {
    [key: string]: any;
    // onChange: (e:ChangeEvent<HTMLInputElement>) => void
};

const formList = (props: Props): JSX.Element => {
    //const lista = ["Luigi ", "Gina ", "Maria ", "Pina "] = 
    const [lista, setNewListItem] = useState([]);
    const input = useRef();
    var addToList = e => {
        e.preventDefault();
        setNewListItem([lista, input.current.value]);
      };
    return <>
        <section>
            <Container>
                <Row>
                    <Col xs lg="4">
                        Form
                        <form onSubmit={addToList}>
                            <label>
                            <input type="text" name="nome" />
                            </label>
                            <input type="submit" value="Submit" ref={input} placeholder="Normal text" />
                        </form>
                    </Col>
                    <Col xs lg="4" >
                        Lista
                        <ul>
                            {lista.map((item, b) => (
                                <li key={b}>{item}</li>
                            ))}
                        </ul>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
}

export default formList
