import React, { useState, useRef } from 'react';
import ReactDOM from "react-dom";
import { Col, Container, Row } from 'react-bootstrap';
// interface Props {
//     [key: string]: any;
//     // onChange: (e:ChangeEvent<HTMLInputElement>) => void
// };


const formList = () => {
    const [newListItem, setNewListItem] = useState([]);
    const [show, setShow] = useState(false);
    const input = useRef();
    const changeOpen = () => setShow(true);
    const changeClose = () => setShow(false);

    var addToList = e => {
        e.preventDefault();
        setNewListItem([...newListItem, input.current.value]);
      };
    return <>
        <section>
            <Container>
                <Row>
                    <Col xs lg="4">
                        Form
                        <form onSubmit={addToList}>
                            <label>
                            <input type="text" name="nome" />
                            </label>
                            <input type="submit" value="Submit" />
                        </form>
                        <ul>
        {newListItem.map((item: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined, b: React.Key | null | undefined) => (
          <li key={b}>{item}</li>
        ))}
      </ul>
                    </Col>
                </Row>
            </Container>
        </section>
    </>
}

export default formList
